import React, { useEffect, useState } from 'react';

const HeroSection = () => {
    const [isVisible, setIsVisible] = useState(false);

    useEffect(() => {
        setIsVisible(true);
    }, []);

    return (
        <section className="relative min-h-screen overflow-hidden" style={{
            backgroundColor: '#f7f9fc',
            backgroundImage: 'radial-gradient(rgba(255, 255, 255, 0.2) 1px, transparent 1px)',
            backgroundSize: '22px 22px'
        }}>
            {/* Background Images */}
            <div className="absolute inset-0 z-0">
                <img
                    src="https://framerusercontent.com/images/lysrb9oLsW8SXarkxi4Kp9N2FM.png"
                    alt="Background"
                    className="w-full h-full object-cover"
                />
                <div className="absolute inset-0 bg-gradient-to-b from-transparent to-white/20"></div>
            </div>

            {/* Pattern Overlay - Petits points blancs subtils avec CSS pur */}
            <div className="absolute inset-0 z-5">
                <div
                    className="absolute inset-0 opacity-15"
                    style={{
                        backgroundImage: 'radial-gradient(circle at center, rgba(255, 255, 255, 0.6) 0.5px, transparent 0.5px)',
                        backgroundSize: '30px 30px',
                        backgroundPosition: '0 0'
                    }}
                ></div>
            </div>

            {/* Light Flare Overlay */}
            <div className="absolute inset-0 z-10">
                <img
                    src="https://framerusercontent.com/images/xdIsSG8TXSulnA0CMVea6YY2Q.png"
                    alt="Light Flare"
                    className="w-full h-full object-cover mix-blend-overlay"
                />
            </div>

            {/* Content */}
            <div className="relative z-20 container mx-auto px-4 sm:px-6 lg:px-8 pt-24 pb-16">
                {/* Main Content */}
                <div className="text-center max-w-4xl mx-auto">
                    {/* Main Heading */}
                    <div className={`mb-6 transition-all duration-1000 delay-300 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-3'
                        }`}>
                        <h1 className="text-4xl sm:text-5xl md:text-6xl lg:text-7xl font-display font-semibold text-gray-800 leading-tight">
                            Tailored AI panels. Quality data.{' '}
                            <span className="text-primary-500 relative">
                                Zero wait.
                            </span>
                        </h1>
                    </div>

                    {/* Subtitle */}
                    <div className={`mb-8 transition-all duration-1000 delay-500 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-3'
                        }`}>
                        <p className="text-lg sm:text-xl text-gray-500 max-w-2xl mx-auto leading-relaxed font-sans">
                            Conduct quantitative and qualitative surveys in real-time with our AI panelists, delivering actionable insights to stimulate growth.
                        </p>
                    </div>

                    {/* CTA Button */}
                    <div className={`transition-all duration-1000 delay-700 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-3'
                        }`}>
                        <a
                            href="/demo"
                            className="inline-flex items-center justify-center px-8 py-4 bg-primary-500 hover:bg-primary-600 text-white font-semibold rounded-lg shadow-lg hover:shadow-xl transform hover:-translate-y-1 transition-all duration-300 text-lg font-display"
                        >
                            Request a demo
                        </a>
                    </div>
                </div>
            </div>

            {/* Bottom Fade */}
            <div className="absolute bottom-0 left-0 right-0 h-32 bg-gradient-to-t from-white to-transparent z-30"></div>
        </section>
    );
};

export default HeroSection;
