import React, { useEffect, useRef, useState } from 'react';
import {
    ChatBubbleLeftRightIcon,
    CheckCircleIcon,
    CogIcon,
    UserGroupIcon,
    ClipboardDocumentListIcon,
    PlusIcon
} from '@heroicons/react/24/outline';

const ProcessStep = ({ icon: Icon, title, description, position, delay = 0, connections = [] }) => {
    const [isVisible, setIsVisible] = useState(false);
    const stepRef = useRef(null);

    useEffect(() => {
        const observer = new IntersectionObserver(
            ([entry]) => {
                if (entry.isIntersecting) {
                    setTimeout(() => setIsVisible(true), delay);
                }
            },
            { threshold: 0.1 }
        );

        if (stepRef.current) {
            observer.observe(stepRef.current);
        }

        return () => observer.disconnect();
    }, [delay]);

    const positionClasses = {
        'top-left': 'row-start-1 col-start-1',
        'top-right': 'row-start-1 col-start-3',
        'bottom-left': 'row-start-3 col-start-1',
        'bottom-right': 'row-start-3 col-start-3',
        'center': 'row-start-2 col-start-2'
    };

    return (
        <div
            ref={stepRef}
            className={`${positionClasses[position]} ${isVisible ? 'opacity-100 translate-y-0 scale-100' : 'opacity-0 translate-y-4 scale-95'
                } transition-all duration-700 ease-out relative`}
            style={{ transitionDelay: `${delay}ms` }}
        >
            <div className="bg-white rounded-xl p-4 md:p-6 shadow-lg border border-gray-200 hover:shadow-xl hover:border-[#194471]/30 transition-all duration-300 group relative max-w-xs md:max-w-sm">

                {/* Connection lines */}
                {connections.map((connection, index) => (
                    <div key={index} className="hidden md:block absolute z-10">
                        {connection === 'to-bottom' && (
                            <div className="absolute top-full left-1/2 transform -translate-x-1/2">
                                <div className="w-0.5 h-16 bg-[#194471]"></div>
                                <div className="w-2 h-2 bg-[#194471] rounded-full -mt-1 -ml-0.5"></div>
                            </div>
                        )}
                        {connection === 'to-center' && (
                            <div className="absolute top-1/2 left-full transform -translate-y-1/2">
                                <div className="h-0.5 w-16 bg-[#194471]"></div>
                                <div className="w-2 h-2 bg-[#194471] rounded-full -mt-1 -mr-1 ml-15"></div>
                            </div>
                        )}
                        {connection === 'to-top-right' && (
                            <div className="absolute bottom-1/2 left-full transform translate-y-1/2">
                                <div className="h-0.5 w-16 bg-[#194471] transform -rotate-45 origin-left"></div>
                                <div className="w-2 h-2 bg-[#194471] rounded-full absolute top-0 left-12 transform -translate-y-1"></div>
                            </div>
                        )}
                        {connection === 'to-bottom-right' && (
                            <div className="absolute top-1/2 left-full transform -translate-y-1/2">
                                <div className="h-0.5 w-16 bg-[#194471] transform rotate-45 origin-left"></div>
                                <div className="w-2 h-2 bg-[#194471] rounded-full absolute bottom-0 left-12 transform translate-y-1"></div>
                            </div>
                        )}
                    </div>
                ))}

                <div className="text-left">
                    <div className="flex items-center gap-2 md:gap-3 mb-2 md:mb-3">
                        <div className="w-2 h-2 bg-[#194471] rounded-full"></div>
                        <h3 className="text-base md:text-lg font-display font-semibold text-gray-800">
                            {title}
                        </h3>
                        <div className="ml-auto">
                            <svg className="w-3 h-3 md:w-4 md:h-4 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 5v.01M12 12v.01M12 19v.01M12 6a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2z" />
                            </svg>
                        </div>
                    </div>
                    <p className="text-xs md:text-sm text-gray-600 leading-relaxed font-sans">
                        {description}
                    </p>

                    {/* Multiple choice options */}
                    {position === 'bottom-left' && (
                        <div className="mt-3 md:mt-4 space-y-2">
                            <div className="flex items-center gap-2">
