import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { ChevronDownIcon, Bars3Icon, XMarkIcon } from '@heroicons/react/24/outline';

const Navbar = () => {
    const [isScrolled, setIsScrolled] = useState(false);
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
    const [activeDropdown, setActiveDropdown] = useState(null);

    useEffect(() => {
        const handleScroll = () => {
            setIsScrolled(window.scrollY > 10);
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    const solutions = {
        'By Team': [
            { name: 'For Brands', href: '/solutions/brands', description: 'Power niche go-to-market strategies at scale' },
            { name: 'For Research Providers', href: '/solutions/research-providers', description: 'Deliver more with science-backed augmented insights' }
        ],
        'By Use Case': [
            { name: 'Segmentation and U&A', href: '/solutions/segmentation-ua', description: 'Understand hard-to-reach audiences better' },
            { name: 'Claim Testing', href: '/solutions/claim-testing', description: 'Validate claims with comprehensive insights' },
            { name: 'Opinion', href: '/solutions/opinion', description: 'Reliably analyze underrepresented groups' },
            { name: 'Brand Perception and Satisfaction', href: '/solutions/brand-perception', description: 'Unlock granular insights even with fast studies' }
        ]
    };

    const handleDropdownToggle = (dropdown) => {
        setActiveDropdown(activeDropdown === dropdown ? null : dropdown);
    };

    return (
        <nav className={`fixed top-0 left-0 right-0 z-50 transition-all duration-300 ${isScrolled
            ? 'bg-white/95 backdrop-blur-md shadow-sm border-b border-gray-100'
            : 'bg-transparent'
            }`}>
            <div className="container mx-auto px-4 sm:px-6 lg:px-8">
                <div className="flex items-center justify-between h-16">
                    {/* Logo */}
                    <div className="flex items-center">
                        <Link to="/" className="flex items-center">
                            <div className="w-8 h-8 bg-primary-500 rounded-lg flex items-center justify-center mr-3">
                                <span className="text-white font-display font-semibold text-lg">I</span>
                            </div>
                            <span className="text-xl font-display font-semibold text-gray-800">Intelly</span>
                        </Link>
                    </div>

                    {/* Desktop Navigation */}
                    <div className="hidden md:flex items-center space-x-8">
                        {/* Platform */}
                        <Link
                            to="/platform"
                            className="text-gray-600 hover:text-primary-500 font-sans font-medium transition-colors duration-200"
                        >
                            Platform
                        </Link>

                        {/* EleyaTwin */}
                        <Link
                            to="/eleyatwin"
                            className="text-gray-600 hover:text-primary-500 font-sans font-medium transition-colors duration-200"
                        >
                            EleyaTwin
                        </Link>

                        {/* FranceGPT */}
                        <Link
                            to="/francegpt"
                            className="text-gray-600 hover:text-primary-500 font-sans font-medium transition-colors duration-200"
                        >
                            FranceGPT
                        </Link>

                        {/* Solutions Dropdown */}
                        <div className="relative">
                            <button
                                onClick={() => handleDropdownToggle('solutions')}
                                className="flex items-center text-gray-600 hover:text-primary-500 font-sans font-medium transition-colors duration-200"
                            >
                                Solutions
                                <ChevronDownIcon className={`ml-1 h-4 w-4 transition-transform duration-200 ${activeDropdown === 'solutions' ? 'rotate-180' : ''
                                    }`} />
                            </button>

                            {activeDropdown === 'solutions' && (
                                <div className="absolute top-full left-0 mt-2 w-96 bg-white rounded-xl shadow-lg border border-gray-100 overflow-hidden">
                                    <div className="p-6">
                                        {Object.entries(solutions).map(([category, items]) => (
                                            <div key={category} className="mb-6 last:mb-0">
                                                <h3 className="text-sm font-display font-semibold text-gray-800 mb-3 uppercase tracking-wide">
                                                    {category}
                                                </h3>
                                                <div className="space-y-3">
                                                    {items.map((item) => (
                                                        <Link
                                                            key={item.name}
                                                            to={item.href}
                                                            className="block p-3 rounded-lg hover:bg-gray-25 transition-colors duration-200 group"
                                                            onClick={() => setActiveDropdown(null)}
                                                        >
                                                            <div className="font-display font-medium text-gray-800 group-hover:text-primary-500 transition-colors duration-200">
                                                                {item.name}
                                                            </div>
                                                            <div className="text-sm text-gray-500 mt-1 font-sans">
                                                                {item.description}
                                                            </div>
                                                        </Link>
                                                    ))}
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            )}
                        </div>

                        {/* Blog */}
                        <Link
                            to="/blog"
                            className="text-gray-600 hover:text-primary-500 font-sans font-medium transition-colors duration-200"
                        >
                            Blog
                        </Link>
                    </div>

                    {/* CTA Buttons */}
                    <div className="hidden md:flex items-center space-x-4">
                        <Link
                            to="/contact"
                            className="text-gray-600 hover:text-primary-500 font-sans font-medium transition-colors duration-200"
                        >
                            Contact us
                        </Link>
                        <Link
                            to="/demo"
                            className="bg-primary-500 hover:bg-primary-600 text-white px-6 py-2 rounded-lg font-display font-medium transition-all duration-200 shadow-sm hover:shadow-md"
                        >
                            Request a Demo
                        </Link>
                    </div>

                    {/* Mobile menu button */}
                    <div className="md:hidden">
                        <button
                            onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                            className="text-gray-600 hover:text-primary-500 transition-colors duration-200"
                        >
                            {isMobileMenuOpen ? (
                                <XMarkIcon className="h-6 w-6" />
                            ) : (
                                <Bars3Icon className="h-6 w-6" />
                            )}
                        </button>
                    </div>
                </div>

                {/* Mobile Navigation */}
                {isMobileMenuOpen && (
                    <div className="md:hidden bg-white border-t border-gray-100 shadow-lg">
                        <div className="px-4 py-6 space-y-4">
                            <Link to="/platform" className="block text-gray-600 hover:text-primary-500 font-sans font-medium py-2" onClick={() => setIsMobileMenuOpen(false)}>
                                Platform
                            </Link>
                            <Link to="/eleyatwin" className="block text-gray-600 hover:text-primary-500 font-sans font-medium py-2" onClick={() => setIsMobileMenuOpen(false)}>
                                EleyaTwin
                            </Link>
                            <Link to="/francegpt" className="block text-gray-600 hover:text-primary-500 font-sans font-medium py-2" onClick={() => setIsMobileMenuOpen(false)}>
                                FranceGPT
                            </Link>

                            <div className="border-t border-gray-100 pt-4">
                                <h3 className="text-sm font-display font-semibold text-gray-800 mb-3">Solutions - By Team</h3>
                                {solutions['By Team'].map((item) => (
                                    <Link key={item.name} to={item.href} className="block text-gray-600 hover:text-primary-500 font-sans py-2 pl-4" onClick={() => setIsMobileMenuOpen(false)}>
                                        {item.name}
                                    </Link>
                                ))}
                            </div>

                            <div className="border-t border-gray-100 pt-4">
                                <h3 className="text-sm font-display font-semibold text-gray-800 mb-3">Solutions - By Use Case</h3>
                                {solutions['By Use Case'].map((item) => (
                                    <Link key={item.name} to={item.href} className="block text-gray-600 hover:text-primary-500 font-sans py-2 pl-4" onClick={() => setIsMobileMenuOpen(false)}>
                                        {item.name}
                                    </Link>
                                ))}
                            </div>

                            <div className="border-t border-gray-100 pt-4">
                                <Link to="/blog" className="block text-gray-600 hover:text-primary-500 font-sans font-medium py-2" onClick={() => setIsMobileMenuOpen(false)}>
                                    Blog
                                </Link>
                            </div>

                            <div className="border-t border-gray-100 pt-4 space-y-3">
                                <Link to="/contact" className="block text-gray-600 hover:text-primary-500 font-sans font-medium" onClick={() => setIsMobileMenuOpen(false)}>
                                    Contact us
                                </Link>
                                <Link to="/demo" className="block bg-primary-500 hover:bg-primary-600 text-white px-6 py-3 rounded-lg font-display font-medium text-center transition-all duration-200" onClick={() => setIsMobileMenuOpen(false)}>
                                    Request a Demo
                                </Link>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </nav>
    );
};

export default Navbar;
