import React, { useEffect, useRef, useState } from 'react';
import {
    ChatBubbleLeftRightIcon,
    CheckCircleIcon,
    CogIcon,
    UserGroupIcon,
    ClipboardDocumentListIcon,
    PlusIcon
} from '@heroicons/react/24/outline';

const ProcessStep = ({ icon: Icon, title, description, position, delay = 0, isConnected = false }) => {
    const [isVisible, setIsVisible] = useState(false);
    const stepRef = useRef(null);

    useEffect(() => {
        const observer = new IntersectionObserver(
            ([entry]) => {
                if (entry.isIntersecting) {
                    setTimeout(() => setIsVisible(true), delay);
                }
            },
            { threshold: 0.1 }
        );

        if (stepRef.current) {
            observer.observe(stepRef.current);
        }

        return () => observer.disconnect();
    }, [delay]);

    const positionClasses = {
        'top': 'lg:col-start-2 lg:row-start-1',
        'right': 'lg:col-start-3 lg:row-start-2',
        'bottom': 'lg:col-start-2 lg:row-start-3',
        'left': 'lg:col-start-1 lg:row-start-2',
        'center': 'lg:col-start-2 lg:row-start-2'
    };

    return (
        <div
            ref={stepRef}
            className={`${positionClasses[position]} ${isVisible ? 'opacity-100 translate-y-0 scale-100' : 'opacity-0 translate-y-4 scale-95'
                } transition-all duration-700 ease-out relative`}
            style={{ transitionDelay: `${delay}ms` }}
        >
            <div className="bg-white rounded-xl p-6 shadow-lg border border-gray-200 hover:shadow-xl hover:border-[#194471]/30 transition-all duration-300 group relative max-w-sm">

                {/* Connection lines to center */}
                {isConnected && (
                    <>
                        {position === 'top' && (
                            <div className="hidden lg:block absolute top-full left-1/2 transform -translate-x-1/2 z-10">
                                <div className="w-0.5 h-12 bg-[#194471]"></div>
                                <div className="w-2 h-2 bg-[#194471] rounded-full -mt-1 -ml-0.5"></div>
                            </div>
                        )}
                        {position === 'right' && (
                            <div className="hidden lg:block absolute right-full top-1/2 transform -translate-y-1/2 z-10">
                                <div className="h-0.5 w-12 bg-[#194471]"></div>
                                <div className="w-2 h-2 bg-[#194471] rounded-full -mt-1 -ml-1"></div>
                            </div>
                        )}
                        {position === 'bottom' && (
                            <div className="hidden lg:block absolute bottom-full left-1/2 transform -translate-x-1/2 z-10">
                                <div className="w-0.5 h-12 bg-[#194471]"></div>
                                <div className="w-2 h-2 bg-[#194471] rounded-full -mb-1 -ml-0.5 mt-11"></div>
                            </div>
                        )}
                        {position === 'left' && (
                            <div className="hidden lg:block absolute left-full top-1/2 transform -translate-y-1/2 z-10">
                                <div className="h-0.5 w-12 bg-[#194471]"></div>
                                <div className="w-2 h-2 bg-[#194471] rounded-full -mt-1 -mr-1 ml-11"></div>
                            </div>
                        )}
                    </>
                )}

                <div className="text-left">
                    <div className="flex items-center gap-3 mb-3">
                        <div className="w-2 h-2 bg-[#194471] rounded-full"></div>
                        <h3 className="text-lg font-display font-semibold text-gray-800">
                            {title}
                        </h3>
                        <div className="ml-auto">
                            <svg className="w-4 h-4 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 5v.01M12 12v.01M12 19v.01M12 6a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2z" />
                            </svg>
                        </div>
                    </div>
                    <p className="text-sm text-gray-600 leading-relaxed font-sans">
                        {description}
                    </p>

                    {/* Multiple choice options */}
                    {position === 'left' && (
                        <div className="mt-4 space-y-2">
                            <div className="flex items-center gap-2">
                                <input type="checkbox" className="w-4 h-4 text-[#194471] rounded border-gray-300 focus:ring-[#194471]" />
                                <span className="text-sm text-gray-600">Option A</span>
                            </div>
                            <div className="flex items-center gap-2">
                                <input type="checkbox" className="w-4 h-4 text-[#194471] rounded border-gray-300 focus:ring-[#194471]" />
                                <span className="text-sm text-gray-600">Option B</span>
                            </div>
                            <div className="flex items-center gap-2">
                                <input type="checkbox" className="w-4 h-4 text-[#194471] rounded border-gray-300 focus:ring-[#194471]" />
                                <span className="text-sm text-gray-400">Other choice</span>
                            </div>
                        </div>
                    )}

                    {/* Option indicators */}
                    {(position === 'right' || position === 'bottom') && (
                        <div className="mt-3">
                            <div className="w-2 h-2 bg-[#194471] rounded-full"></div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

const CentralHub = ({ delay = 0 }) => {
    const [isVisible, setIsVisible] = useState(false);
    const hubRef = useRef(null);

    useEffect(() => {
        const observer = new IntersectionObserver(
            ([entry]) => {
                if (entry.isIntersecting) {
                    setTimeout(() => setIsVisible(true), delay);
                }
            },
            { threshold: 0.1 }
        );

        if (hubRef.current) {
            observer.observe(hubRef.current);
        }

        return () => observer.disconnect();
    }, [delay]);

    return (
        <div
