import React, { useEffect, useRef, useState } from 'react';
import {
    ChatBubbleLeftRightIcon,
    CheckCircleIcon,
    CogIcon,
    UserGroupIcon,
    ClipboardDocumentListIcon,
    PlusIcon
} from '@heroicons/react/24/outline';

const ProcessStep = ({ icon: Icon, title, description, position, delay = 0, isConnected = false }) => {
    const [isVisible, setIsVisible] = useState(false);
    const stepRef = useRef(null);

    useEffect(() => {
        const observer = new IntersectionObserver(
            ([entry]) => {
                if (entry.isIntersecting) {
                    setTimeout(() => setIsVisible(true), delay);
                }
            },
            { threshold: 0.1 }
        );

        if (stepRef.current) {
            observer.observe(stepRef.current);
        }

        return () => observer.disconnect();
    }, [delay]);

    const positionClasses = {
        'top': 'lg:col-start-2 lg:row-start-1',
        'right': 'lg:col-start-3 lg:row-start-2',
        'bottom': 'lg:col-start-2 lg:row-start-3',
        'left': 'lg:col-start-1 lg:row-start-2',
        'center': 'lg:col-start-2 lg:row-start-2'
    };

    return (
        <div
            ref={stepRef}
            className={`${positionClasses[position]} ${isVisible ? 'opacity-100 translate-y-0 scale-100' : 'opacity-0 translate-y-4 scale-95'
                } transition-all duration-700 ease-out`}
            style={{ transitionDelay: `${delay}ms` }}
        >
            <div className="bg-white rounded-xl p-6 shadow-lg border border-blue-100 hover:shadow-xl hover:border-blue-200 transition-all duration-300 group relative">
                {/* Connection lines */}
                {isConnected && (
                    <>
                        {/* Ligne vers le centre - visible uniquement sur desktop */}
                        <div className="hidden lg:block absolute inset-0 pointer-events-none">
                            {position === 'top' && (
                                <div className="absolute top-full left-1/2 w-px h-6 bg-gradient-to-b from-blue-300 to-transparent transform -translate-x-1/2"></div>
                            )}
                            {position === 'right' && (
                                <div className="absolute right-full top-1/2 w-6 h-px bg-gradient-to-l from-blue-300 to-transparent transform -translate-y-1/2"></div>
                            )}
                            {position === 'bottom' && (
                                <div className="absolute bottom-full left-1/2 w-px h-6 bg-gradient-to-t from-blue-300 to-transparent transform -translate-x-1/2"></div>
                            )}
                            {position === 'left' && (
                                <div className="absolute left-full top-1/2 w-6 h-px bg-gradient-to-r from-blue-300 to-transparent transform -translate-y-1/2"></div>
                            )}
                        </div>
                    </>
                )}

                <div className="text-center">
                    <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center mx-auto mb-4 group-hover:bg-blue-200 transition-colors duration-300">
                        <Icon className="w-6 h-6 text-blue-600" />
                    </div>
                    <h3 className="text-lg font-display font-semibold text-gray-800 mb-2">
                        {title}
                    </h3>
                    <p className="text-sm text-gray-600 leading-relaxed font-sans">
                        {description}
                    </p>
                </div>
            </div>
        </div>
    );
};

const CentralHub = ({ delay = 0 }) => {
    const [isVisible, setIsVisible] = useState(false);
    const hubRef = useRef(null);

    useEffect(() => {
        const observer = new IntersectionObserver(
            ([entry]) => {
                if (entry.isIntersecting) {
                    setTimeout(() => setIsVisible(true), delay);
                }
            },
            { threshold: 0.1 }
        );

        if (hubRef.current) {
            observer.observe(hubRef.current);
        }

        return () => observer.disconnect();
    }, [delay]);

    return (
        <div
            ref={hubRef}
            className={`lg:col-start-2 lg:row-start-2 ${isVisible ? 'opacity-100 scale-100' : 'opacity-0 scale-75'
                } transition-all duration-1000 ease-out`}
            style={{ transitionDelay: `${delay}ms` }}
        >
            <div className="relative">
                {/* Background glow effect */}
                <div className="absolute inset-0 bg-gradient-to-r from-blue-400 to-blue-600 rounded-2xl blur-lg opacity-20 scale-110"></div>

                {/* Main hub */}
                <div className="relative bg-gradient-to-r from-blue-500 to-blue-600 rounded-2xl p-8 shadow-2xl border border-blue-400">
                    <div className="text-center">
                        <div className="w-16 h-16 bg-white/20 rounded-xl flex items-center justify-center mx-auto mb-4 backdrop-blur-sm">
                            <CogIcon className="w-8 h-8 text-white animate-spin-slow" />
                        </div>
                        <h3 className="text-2xl font-display font-bold text-white mb-2">
                            AI Integrated
                        </h3>
                        <p className="text-blue-100 text-sm font-sans">
                            Process & Analyze
                        </p>
                    </div>
                </div>
            </div>
        </div>
    );
};

const ValueSection = () => {
    const [titleVisible, setTitleVisible] = useState(false);
    const titleRef = useRef(null);

    useEffect(() => {
        const observer = new IntersectionObserver(
            ([entry]) => {
                if (entry.isIntersecting) {
                    setTitleVisible(true);
                }
            },
            { threshold: 0.1 }
        );

        if (titleRef.current) {
            observer.observe(titleRef.current);
        }

        return () => observer.disconnect();
    }, []);

    const processSteps = [
        {
            icon: ChatBubbleLeftRightIcon,
            title: "Entry Gate",
            description: "Hi, my name is Int√©lly. Is that anything that I can help you for?",
            position: "top",
            isConnected: true
        },
        {
            icon: CheckCircleIcon,
            title: "Option 1",
            description: "This is the solution",
            position: "right",
            isConnected: true
        },
        {
            icon: UserGroupIcon,
            title: "Option 2",
            description: "Maybe you should do...",
            position: "bottom",
            isConnected: true
        },
        {
            icon: ClipboardDocumentListIcon,
            title: "Multiple Choice",
            description: "Option A, Option B, Other choice",
            position: "left",
            isConnected: true
        }
    ];

    return (
        <section className="relative py-20" style={{
            backgroundColor: 'transparent'
        }}>
            {/* Gradient transitions */}
            <div className="absolute top-0 left-0 right-0 h-20 bg-gradient-to-b from-white/40 via-white/70 to-white/90 z-10"></div>

            {/* Main content */}
            <div className="relative z-20" style={{
                backgroundColor: 'rgba(255, 255, 255, 0.95)',
                backgroundImage: 'radial-gradient(rgba(59, 130, 246, 0.08) 1px, transparent 1px)',
                backgroundSize: '24px 24px',
                margin: '20px 0'
            }}>
                <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-20">
                    {/* Header */}
                    <div
                        ref={titleRef}
                        className={`text-center mb-16 transition-all duration-1000 ${titleVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'
                            }`}
                    >
                        <h2 className="text-3xl sm:text-4xl lg:text-5xl font-display font-semibold text-gray-800 mb-6">
                            Skip recruitment, test ideas on{' '}
                            <span className="text-blue-600">AI-mirrored audiences</span>
                        </h2>
                        <p className="text-lg text-gray-600 max-w-3xl mx-auto font-sans mb-8">
                            Experience our intelligent process flow that connects users to AI-powered solutions
                        </p>
                    </div>

                    {/* Process Diagram */}
                    <div className="max-w-4xl mx-auto mb-16">
                        {/* Mobile layout - vertical stack */}
                        <div className="lg:hidden space-y-6">
                            <CentralHub delay={200} />
                            {processSteps.map((step, index) => (
                                <ProcessStep
                                    key={index}
                                    icon={step.icon}
                                    title={step.title}
                                    description={step.description}
                                    position="center"
                                    delay={300 + index * 100}
                                    isConnected={false}
                                />
                            ))}
                        </div>

                        {/* Desktop layout - grid */}
                        <div className="hidden lg:grid grid-cols-3 grid-rows-3 gap-6 items-center min-h-[400px]">
                            {/* Central Hub */}
                            <CentralHub delay={200} />

                            {/* Process Steps */}
                            {processSteps.map((step, index) => (
                                <ProcessStep
                                    key={index}
                                    icon={step.icon}
                                    title={step.title}
                                    description={step.description}
                                    position={step.position}
                                    delay={300 + index * 150}
                                    isConnected={step.isConnected}
                                />
                            ))}
                        </div>
                    </div>

                    {/* Add more button */}
                    <div className="text-center">
                        <button className="inline-flex items-center gap-2 bg-white border-2 border-blue-200 text-blue-600 px-6 py-3 rounded-lg hover:bg-blue-50 hover:border-blue-300 transition-all duration-300 font-display font-medium">
                            <PlusIcon className="w-5 h-5" />
                            Explore More Options
                        </button>
                    </div>
                </div>
            </div>

            {/* Bottom gradient */}
            <div className="absolute bottom-0 left-0 right-0 h-20 bg-gradient-to-t from-white/40 via-white/70 to-white/90 z-10"></div>

            <style jsx>{`
                @keyframes spin-slow {
                    from { transform: rotate(0deg); }
                    to { transform: rotate(360deg); }
                }
                .animate-spin-slow {
                    animation: spin-slow 8s linear infinite;
                }
            `}</style>
        </section>
    );
};

export default ValueSection;
