import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { ChevronDownIcon, Bars3Icon, XMarkIcon } from '@heroicons/react/24/outline';
import eleyaLogo from '../assets/images/logo11.png';

const Navbar = () => {
    const [isScrolled, setIsScrolled] = useState(false);
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            setIsScrolled(window.scrollY > 50);
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    // Fonction pour remonter en haut de la page
    const scrollToTop = () => {
        window.scrollTo({ top: 0, behavior: 'smooth' });
        setIsMobileMenuOpen(false);
    };

    const platformItems = [
        { name: 'Plateforme Overview', href: '/platform', description: 'Vue d\'ensemble de notre plateforme d\'analytics IA' },
        { name: 'EleyaTwin', href: '/eleyatwin', description: 'Notre plateforme de jumeaux numériques' },
        { name: 'FranceGPT', href: '/francegpt', description: 'IA conversationnelle française' }
    ];

    const solutions = {
        'By Team': [
            { name: 'For Brands', href: '/solutions/brands', description: 'Power niche go-to-market strategies at scale' },
            { name: 'For Research Providers', href: '/solutions/research-providers', description: 'Deliver more with science-backed augmented insights' }
        ],
        'By Use Case': [
            { name: 'Segmentation and U&A', href: '/solutions/segmentation-ua', description: 'Understand hard-to-reach audiences better' },
            { name: 'Claim Testing', href: '/solutions/claim-testing', description: 'Validate claims with comprehensive insights' },
            { name: 'Opinion', href: '/solutions/opinion', description: 'Reliably analyze underrepresented groups' },
            { name: 'Brand Perception and Satisfaction', href: '/solutions/brand-perception', description: 'Unlock granular insights even with fast studies' }
        ]
    };

    const otherPages = [
        { name: 'Blog', href: '/blog', description: 'Insights et actualités' }
    ];

    return (
        <nav className="fixed top-0 left-0 w-full z-50 transition-all duration-500">
            <div className="w-full px-4 sm:px-6 lg:px-8 pt-6">
                <div className={`transition-all duration-500 ${isScrolled
                        ? 'max-w-2xl mx-auto bg-white/90 backdrop-blur-xl shadow-lg border border-gray-100/50 rounded-full px-4'
                        : 'max-w-7xl mx-auto bg-white/5 backdrop-blur-sm border border-white/20 rounded-2xl px-6'
                    }`}>
                    <div className="flex items-center justify-between h-16">
                        {/* Logo */}
                        <div className="flex items-center">
                            <Link to="/" className="flex items-center" onClick={scrollToTop}>
                                <img
                                    src={eleyaLogo}
                                    alt="Eleya Logo"
                                    className={`w-auto object-contain transition-all duration-500 ${isScrolled ? 'h-8' : 'h-10'
                                        }`}
                                />
                            </Link>
                        </div>

                        {/* Desktop Navigation - Toujours au centre */}
                        <div className="hidden md:flex items-center">
                            <div className={`flex items-center transition-all duration-500 ${isScrolled ? 'space-x-4' : 'space-x-8'
                                }`}>
                                {/* Plateforme Dropdown */}
                                <div className="relative group">
                                    <button className={`flex items-center font-medium transition-all duration-300 ${isScrolled
                                            ? 'text-gray-700 hover:text-[#194471] text-sm'
                                            : 'text-gray-800 hover:text-[#194471]'
                                        }`}>
                                        Plateforme
                                        <ChevronDownIcon className="ml-1 h-4 w-4 transition-transform duration-200 group-hover:rotate-180" />
                                    </button>

                                    <div className="absolute top-full left-0 pt-2 w-80 opacity-0 invisible group-hover:opacity-100 group-hover:visible transition-all duration-200">
                                        <div className="bg-white rounded-2xl shadow-xl border border-gray-100/50 overflow-hidden backdrop-blur-xl">
                                            <div className="p-6">
                                                <div className="space-y-2">
                                                    {platformItems.map((item) => (
                                                        <Link
                                                            key={item.name}
                                                            to={item.href}
                                                            onClick={scrollToTop}
                                                            className="block p-3 rounded-xl hover:bg-[#194471]/5 transition-colors duration-200 group/item"
                                                        >
                                                            <div className="font-medium text-gray-800 group-hover/item:text-[#194471] transition-colors duration-200">
                                                                {item.name}
                                                            </div>
                                                            <div className="text-sm text-gray-500 mt-1">
                                                                {item.description}
                                                            </div>
                                                        </Link>
                                                    ))}
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>

                                {/* Solutions Dropdown */}
                                <div className="relative group">
                                    <button className={`flex items-center font-medium transition-all duration-300 ${isScrolled
                                            ? 'text-gray-700 hover:text-[#194471] text-sm'
                                            : 'text-gray-800 hover:text-[#194471]'
                                        }`}>
                                        Solutions
                                        <ChevronDownIcon className="ml-1 h-4 w-4 transition-transform duration-200 group-hover:rotate-180" />
                                    </button>

                                    <div className="absolute top-full left-0 pt-2 w-96 opacity-0 invisible group-hover:opacity-100 group-hover:visible transition-all duration-200">
                                        <div className="bg-white rounded-2xl shadow-xl border border-gray-100/50 overflow-hidden backdrop-blur-xl">
                                            <div className="p-6">
                                                {Object.entries(solutions).map(([category, items]) => (
                                                    <div key={category} className="mb-6 last:mb-0">
                                                        <h3 className="text-sm font-semibold text-gray-800 mb-3 uppercase tracking-wide">
                                                            {category}
                                                        </h3>
                                                        <div className="space-y-2">
                                                            {items.map((item) => (
                                                                <Link
                                                                    key={item.name}
                                                                    to={item.href}
                                                                    onClick={scrollToTop}
                                                                    className="block p-3 rounded-xl hover:bg-[#194471]/5 transition-colors duration-200 group/item"
                                                                >
                                                                    <div className="font-medium text-gray-800 group-hover/item:text-[#194471] transition-colors duration-200">
                                                                        {item.name}
                                                                    </div>
                                                                    <div className="text-sm text-gray-500 mt-1">
                                                                        {item.description}
                                                                    </div>
                                                                </Link>
                                                            ))}
                                                        </div>
                                                    </div>
                                                ))}
                                            </div>
                                        </div>
                                    </div>
                                </div>

                                {/* Other Pages */}
                                {otherPages.map((page) => (
                                    <Link
                                        key={page.name}
                                        to={page.href}
                                        onClick={scrollToTop}
                                        className={`font-medium transition-all duration-300 ${isScrolled
                                                ? 'text-gray-700 hover:text-[#194471] text-sm'
                                                : 'text-gray-800 hover:text-[#194471]'
                                            }`}
                                    >
                                        {page.name}
                                    </Link>
                                ))}
                            </div>
                        </div>

                        {/* CTA Button - Toujours à droite */}
                        <div className="hidden md:flex items-center">
                            <Link
                                to="/demo"
                                onClick={scrollToTop}
                                className={`rounded-full font-medium bg-[#194471] hover:bg-[#194471]/90 text-white shadow-lg hover:shadow-xl transition-all duration-500 ${isScrolled ? 'px-4 py-2 text-sm' : 'px-6 py-2.5'
                                    }`}
                            >
                                {isScrolled ? 'Demo' : 'Request a Demo'}
                            </Link>
                        </div>

                        {/* Mobile menu button */}
                        <div className="md:hidden">
                            <button
                                onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                                className={`transition-colors duration-300 ${isScrolled
                                        ? 'text-gray-700 hover:text-[#194471]'
                                        : 'text-gray-800 hover:text-[#194471]'
                                    }`}
                            >
                                {isMobileMenuOpen ? (
                                    <XMarkIcon className="h-6 w-6" />
                                ) : (
                                    <Bars3Icon className="h-6 w-6" />
                                )}
                            </button>
                        </div>
                    </div>

                    {/* Mobile Navigation */}
                    {isMobileMenuOpen && (
                        <div className="md:hidden mt-4 mb-6">
                            <div className="bg-white/95 backdrop-blur-xl rounded-2xl shadow-xl border border-gray-100/50 p-6 space-y-4">
                                {/* Platform mobile */}
                                <div className="border-b border-gray-100 pb-4">
                                    <h3 className="text-sm font-semibold text-gray-800 mb-3">Plateforme</h3>
                                    {platformItems.map((item) => (
                                        <Link key={item.name} to={item.href} className="block text-gray-600 hover:text-[#194471] py-2 pl-4 transition-colors" onClick={scrollToTop}>
                                            {item.name}
                                        </Link>
                                    ))}
                                </div>

                                {/* Solutions mobile */}
                                <div className="border-b border-gray-100 pb-4">
                                    <h3 className="text-sm font-semibold text-gray-800 mb-3">Solutions - By Team</h3>
                                    {solutions['By Team'].map((item) => (
                                        <Link key={item.name} to={item.href} className="block text-gray-600 hover:text-[#194471] py-2 pl-4 transition-colors" onClick={scrollToTop}>
                                            {item.name}
                                        </Link>
                                    ))}
                                </div>

                                <div className="border-b border-gray-100 pb-4">
                                    <h3 className="text-sm font-semibold text-gray-800 mb-3">Solutions - By Use Case</h3>
                                    {solutions['By Use Case'].map((item) => (
                                        <Link key={item.name} to={item.href} className="block text-gray-600 hover:text-[#194471] py-2 pl-4 transition-colors" onClick={scrollToTop}>
                                            {item.name}
                                        </Link>
                                    ))}
                                </div>

                                {/* Other Pages mobile */}
                                <div className="border-b border-gray-100 pb-4">
                                    <h3 className="text-sm font-semibold text-gray-800 mb-3">Autres Pages</h3>
                                    {otherPages.map((page) => (
                                        <Link key={page.name} to={page.href} className="block text-gray-600 hover:text-[#194471] py-2 pl-4 transition-colors" onClick={scrollToTop}>
                                            {page.name}
                                        </Link>
                                    ))}
                                </div>

                                <div className="pt-4">
                                    <Link to="/demo" className="block bg-[#194471] hover:bg-[#194471]/90 text-white px-6 py-3 rounded-full font-medium text-center transition-all duration-200 shadow-md" onClick={scrollToTop}>
                                        Request a Demo
                                    </Link>
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
