import React, { useEffect, useRef, useState } from 'react';

const AIIntegratedSection = () => {
    const [isVisible, setIsVisible] = useState(false);
    const sectionRef = useRef(null);

    useEffect(() => {
        const observer = new IntersectionObserver(
            ([entry]) => {
                if (entry.isIntersecting) {
                    setIsVisible(true);
                }
            },
            { threshold: 0.1 }
        );

        if (sectionRef.current) {
            observer.observe(sectionRef.current);
        }

        return () => observer.disconnect();
    }, []);

    return (
        <section
            ref={sectionRef}
            className="relative py-20 overflow-hidden"
            style={{
                backgroundColor: 'rgba(255, 255, 255, 0.95)',
                backgroundImage: 'radial-gradient(rgba(254, 195, 147, 0.08) 1px, transparent 1px)',
                backgroundSize: '20px 20px'
            }}
        >
            <div className="container mx-auto px-4 sm:px-6 lg:px-8">
                <div className="max-w-6xl mx-auto">
                    {/* AI Diagram Container */}
                    <div
                        className={`relative h-96 md:h-[500px] transition-all duration-1000 ${isVisible ? 'opacity-100 scale-100' : 'opacity-0 scale-95'
                            }`}
                    >
                        {/* Connection Lines with Flowing Animation */}
                        <svg
                            className="absolute inset-0 w-full h-full"
                            style={{ zIndex: 1 }}
                        >
                            {/* Animated gradient for electric current effect */}
                            <defs>
                                <linearGradient id="flowGradient1" x1="0%" y1="0%" x2="100%" y2="0%">
                                    <stop offset="0%" stopColor="transparent" />
                                    <stop offset="30%" stopColor="#5B73EB" stopOpacity="0.8" />
                                    <stop offset="70%" stopColor="#194471" stopOpacity="1" />
                                    <stop offset="100%" stopColor="transparent" />
                                    <animateTransform
                                        attributeName="gradientTransform"
                                        type="translate"
                                        values="-100 0;200 0;-100 0"
                                        dur="3s"
                                        repeatCount="indefinite"
                                    />
                                </linearGradient>
                                <linearGradient id="flowGradient2" x1="0%" y1="0%" x2="100%" y2="100%">
                                    <stop offset="0%" stopColor="transparent" />
                                    <stop offset="30%" stopColor="#5B73EB" stopOpacity="0.8" />
                                    <stop offset="70%" stopColor="#194471" stopOpacity="1" />
                                    <stop offset="100%" stopColor="transparent" />
                                    <animateTransform
                                        attributeName="gradientTransform"
                                        type="translate"
                                        values="-100 -100;200 200;-100 -100"
                                        dur="2.5s"
                                        repeatCount="indefinite"
                                    />
                                </linearGradient>
                                <linearGradient id="flowGradient3" x1="0%" y1="100%" x2="100%" y2="0%">
                                    <stop offset="0%" stopColor="transparent" />
                                    <stop offset="30%" stopColor="#5B73EB" stopOpacity="0.8" />
                                    <stop offset="70%" stopColor="#194471" stopOpacity="1" />
                                    <stop offset="100%" stopColor="transparent" />
                                    <animateTransform
                                        attributeName="gradientTransform"
                                        type="translate"
                                        values="-100 100;200 -200;-100 100"
                                        dur="3.5s"
                                        repeatCount="indefinite"
                                    />
                                </linearGradient>
                            </defs>

                            {/* Connection Lines */}
                            {/* Entry Gate to AI Center */}
                            <line
                                x1="10%" y1="20%"
                                x2="45%" y2="45%"
                                stroke="url(#flowGradient1)"
                                strokeWidth="3"
                                className="opacity-70"
                            />

                            {/* Multiple Choice to AI Center */}
                            <line
                                x1="15%" y1="70%"
                                x2="45%" y2="55%"
                                stroke="url(#flowGradient2)"
                                strokeWidth="3"
                                className="opacity-70"
                            />

                            {/* AI Center to Option 1 */}
                            <line
                                x1="55%" y1="45%"
                                x2="85%" y2="25%"
                                stroke="url(#flowGradient3)"
                                strokeWidth="3"
                                className="opacity-70"
                            />

                            {/* AI Center to Option 2 */}
                            <line
                                x1="55%" y1="55%"
                                x2="85%" y2="75%"
                                stroke="url(#flowGradient1)"
                                strokeWidth="3"
                                className="opacity-70"
                            />

                            {/* Additional flowing connections */}
                            <circle cx="50%" cy="50%" r="2" fill="#194471">
                                <animate attributeName="r" values="2;4;2" dur="2s" repeatCount="indefinite" />
                                <animate attributeName="opacity" values="0.5;1;0.5" dur="2s" repeatCount="indefinite" />
                            </circle>
