import React, { useEffect, useState } from 'react';
import { ChevronRightIcon } from '@heroicons/react/24/outline';

const HeroSection = () => {
    const [isVisible, setIsVisible] = useState(false);

    useEffect(() => {
        setIsVisible(true);
    }, []);

    return (
        <section className="relative min-h-screen bg-white overflow-hidden">
            {/* Background Images */}
            <div className="absolute inset-0 z-0">
                <img
                    src="https://framerusercontent.com/images/lysrb9oLsW8SXarkxi4Kp9N2FM.png"
                    alt="Background"
                    className="w-full h-full object-cover"
                />
                <div className="absolute inset-0 bg-gradient-to-b from-transparent to-white/20"></div>
            </div>

            {/* Light Flare Overlay */}
            <div className="absolute inset-0 z-10">
                <img
                    src="https://framerusercontent.com/images/xdIsSG8TXSulnA0CMVea6YY2Q.png"
                    alt="Light Flare"
                    className="w-full h-full object-cover mix-blend-overlay"
                />
            </div>

            {/* Content */}
            <div className="relative z-20 container mx-auto px-4 sm:px-6 lg:px-8 pt-8 pb-16">
                {/* Main Content */}
                <div className="text-center max-w-4xl mx-auto">
                    {/* Main Heading */}
                    <div className={`mb-6 transition-all duration-1000 delay-300 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-3'
                        }`}>
                        <h1 className="text-4xl sm:text-5xl md:text-6xl lg:text-7xl font-bold text-gray-900 leading-tight">
                            Redefine Analytics with our{' '}
                            <span className="text-blue-600 relative">
                                AI
                                {/* Cursor Animation */}
                                <div className="absolute -top-2 -right-8 flex items-center gap-1 text-sm bg-black text-white px-2 py-1 rounded shadow-lg animate-pulse">
                                    <span>You</span>
                                    <svg className="w-3 h-3" viewBox="0 0 13 15" fill="currentColor">
                                        <path d="M0 0L13 7.5L0 15V0Z" />
                                    </svg>
                                </div>
                            </span>
                        </h1>
                    </div>

                    {/* Subtitle */}
                    <div className={`mb-8 transition-all duration-1000 delay-500 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-3'
                        }`}>
                        <p className="text-lg sm:text-xl text-gray-600 max-w-2xl mx-auto leading-relaxed">
                            Gain access to real-time analytics and actionable insights,
                            empowering you to make informed decisions
                        </p>
                    </div>

                    {/* CTA Button */}
                    <div className={`transition-all duration-1000 delay-700 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-3'
                        }`}>
                        <a
                            href="/pricing"
                            className="inline-flex items-center justify-center px-8 py-4 bg-gradient-to-r from-blue-700 to-blue-600 text-white font-semibold rounded-lg shadow-lg hover:shadow-xl transform hover:-translate-y-1 transition-all duration-300 text-lg"
                        >
                            Book a demo
                        </a>
                    </div>
                </div>
            </div>

            {/* Bottom Fade */}
            <div className="absolute bottom-0 left-0 right-0 h-32 bg-gradient-to-t from-white to-transparent z-30"></div>
        </section>
    );
};

export default HeroSection;
