import React, { useEffect, useRef, useState } from 'react';
import { CalendarIcon, ArrowRightIcon } from '@heroicons/react/24/outline';

// Composants d'illustration SVG
const AIAnalyticsIllustration = () => (
    <svg viewBox="0 0 200 120" className="w-full h-full">
        <defs>
            <linearGradient id="aiGradient" x1="0%" y1="0%" x2="100%" y2="100%">
                <stop offset="0%" stopColor="#3B82F6" />
                <stop offset="100%" stopColor="#1D4ED8" />
            </linearGradient>
        </defs>
        <rect width="200" height="120" fill="url(#aiGradient)" opacity="0.1" />
        <circle cx="50" cy="60" r="15" fill="#3B82F6" opacity="0.8" />
        <circle cx="100" cy="40" r="10" fill="#1D4ED8" opacity="0.6" />
        <circle cx="150" cy="70" r="12" fill="#3B82F6" opacity="0.7" />
        <path d="M40 60 L90 40 L140 70" stroke="#3B82F6" strokeWidth="2" fill="none" />
        <rect x="170" y="20" width="20" height="80" fill="#1D4ED8" opacity="0.3" />
        <rect x="10" y="30" width="15" height="60" fill="#3B82F6" opacity="0.4" />
        <text x="100" y="90" textAnchor="middle" className="text-xs font-medium" fill="#1D4ED8">AI Analytics</text>
    </svg>
);

const MetricsIllustration = () => (
    <svg viewBox="0 0 200 120" className="w-full h-full">
        <defs>
            <linearGradient id="metricsGradient" x1="0%" y1="0%" x2="100%" y2="100%">
                <stop offset="0%" stopColor="#10B981" />
                <stop offset="100%" stopColor="#059669" />
            </linearGradient>
        </defs>
        <rect width="200" height="120" fill="url(#metricsGradient)" opacity="0.1" />
        <rect x="20" y="80" width="15" height="30" fill="#10B981" />
        <rect x="45" y="60" width="15" height="50" fill="#059669" />
