import React, { useEffect, useRef, useState } from 'react';

const CounterCard = ({ number, suffix, title, description, delay = 0 }) => {
    const [isVisible, setIsVisible] = useState(false);
    const [count, setCount] = useState(0);
    const cardRef = useRef(null);

    useEffect(() => {
        const observer = new IntersectionObserver(
            ([entry]) => {
                if (entry.isIntersecting) {
                    setTimeout(() => {
                        setIsVisible(true);
                        // Start counting animation
                        const duration = 2000; // 2 seconds
                        const steps = 60;
                        const increment = number / steps;
                        let current = 0;

                        const timer = setInterval(() => {
                            current += increment;
                            if (current >= number) {
                                setCount(number);
                                clearInterval(timer);
                            } else {
                                setCount(Math.floor(current));
                            }
                        }, duration / steps);
                    }, delay);
                }
            },
            { threshold: 0.1 }
        );

        if (cardRef.current) {
            observer.observe(cardRef.current);
        }

        return () => observer.disconnect();
    }, [number, delay]);

    return (
        <div
            ref={cardRef}
            className={`text-center transition-all duration-1000 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'
                }`}
        >
            <div className="text-4xl sm:text-5xl lg:text-6xl font-display font-semibold text-primary-500 mb-2">
                {count.toLocaleString()}{suffix}
            </div>
            <h3 className="text-xl font-display font-semibold text-gray-800 mb-2">
                {title}
            </h3>
            <p className="text-gray-500 font-sans">
                {description}
            </p>
        </div>
    );
};

const FiguresSection = () => {
    const [titleVisible, setTitleVisible] = useState(false);
    const titleRef = useRef(null);

    useEffect(() => {
        const observer = new IntersectionObserver(
            ([entry]) => {
                if (entry.isIntersecting) {
                    setTitleVisible(true);
                }
            },
            { threshold: 0.1 }
        );

        if (titleRef.current) {
            observer.observe(titleRef.current);
        }

        return () => observer.disconnect();
    }, []);

    const figures = [
        {
            number: 50000,
            suffix: '+',
            title: 'Active Users',
            description: 'Businesses trust Intelly for their analytics needs'
        },
        {
            number: 99,
            suffix: '%',
            title: 'Uptime',
            description: 'Reliable service you can count on 24/7'
        },
        {
            number: 2,
            suffix: 'M+',
            title: 'Data Points',
            description: 'Processed daily to deliver accurate insights'
        },
        {
            number: 150,
            suffix: '+',
            title: 'Countries',
            description: 'Global reach with local insights'
        }
    ];

    return (
        <section className="py-20 bg-white">
            <div className="container mx-auto px-4 sm:px-6 lg:px-8">
                {/* Header */}
                <div
                    ref={titleRef}
                    className={`text-center mb-16 transition-all duration-1000 ${titleVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'
                        }`}
                >
                    <p className="text-primary-500 text-sm font-medium uppercase tracking-wide mb-4 font-sans">
                        Our Impact
                    </p>
                    <h2 className="text-3xl sm:text-4xl lg:text-5xl font-display font-semibold text-gray-800 mb-6">
                        Trusted by businesses worldwide
                    </h2>
                    <p className="text-lg text-gray-500 max-w-3xl mx-auto font-sans">
                        Join thousands of companies that have transformed their decision-making
                        with our AI-powered analytics platform.
                    </p>
                </div>

                {/* Figures Grid */}
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8 lg:gap-12">
                    {figures.map((figure, index) => (
                        <CounterCard
                            key={index}
                            number={figure.number}
                            suffix={figure.suffix}
                            title={figure.title}
                            description={figure.description}
                            delay={index * 200}
                        />
                    ))}
                </div>

                {/* Testimonial */}
                <div className={`mt-20 transition-all duration-1000 delay-800 ${titleVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'
                    }`}>
                    <div className="bg-gradient-to-r from-primary-50 to-primary-100 rounded-2xl p-8 lg:p-12 text-center">
                        <blockquote className="text-xl lg:text-2xl font-display font-medium text-gray-800 mb-6">
                            "Intelly has revolutionized how we understand our customers.
                            The AI insights have helped us increase conversions by 40% in just 3 months."
                        </blockquote>
                        <div className="flex items-center justify-center gap-4">
                            <div className="w-12 h-12 bg-primary-500 rounded-full flex items-center justify-center">
                                <span className="text-white font-semibold font-display">JS</span>
                            </div>
                            <div className="text-left">
                                <div className="font-semibold text-gray-800 font-display">John Smith</div>
                                <div className="text-gray-500 font-sans">CEO, TechCorp</div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
};

export default FiguresSection;
