import React, { useEffect, useState } from 'react';

const HeroSection = () => {
    const [isVisible, setIsVisible] = useState(false);

    useEffect(() => {
        setIsVisible(true);
    }, []);

    return (
        <section className="relative min-h-screen overflow-hidden -mt-16">
            {/* Modern background with dot pattern */}
            <div className="absolute inset-0 dot-pattern opacity-30"></div>

            {/* Gradient overlay for depth */}
            <div className="absolute inset-0 bg-gradient-to-br from-white/40 via-transparent to-blue-50/20"></div>

            {/* Content */}
            <div className="relative z-20 container mx-auto px-4 sm:px-6 lg:px-8 pt-48 pb-16">
                {/* Main Content */}
                <div className="text-center max-w-4xl mx-auto">
                    {/* Main Heading */}
                    <div className={`mb-6 transition-all duration-1000 delay-300 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-3'
                        }`}>
                        <h1 className="text-4xl sm:text-5xl md:text-6xl lg:text-7xl font-display font-semibold text-gray-800 leading-tight">
                            Tailored AI panels. Quality data.{' '}
                            <span className="text-[#194471] relative bg-gradient-to-r from-[#194471] to-[#5b71eb] bg-clip-text text-transparent">
                                Zero wait.
                            </span>
                        </h1>
                    </div>

                    {/* Subtitle */}
                    <div className={`mb-8 transition-all duration-1000 delay-500 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-3'
                        }`}>
                        <p className="text-lg sm:text-xl text-gray-500 max-w-2xl mx-auto leading-relaxed font-sans">
                            Conduct quantitative and qualitative surveys in real-time with our AI panelists, delivering actionable insights to stimulate growth.
                        </p>
                    </div>

                    {/* CTA Button */}
                    <div className={`transition-all duration-1000 delay-700 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-3'
                        }`}>
                        <a
                            href="/demo"
                            className="inline-flex items-center justify-center px-8 py-4 bg-gradient-to-r from-[#194471] to-[#5b71eb] hover:from-[#194471]/90 hover:to-[#5b71eb]/90 text-white font-semibold rounded-lg shadow-lg hover:shadow-xl transform hover:-translate-y-1 transition-all duration-300 text-lg font-display"
                        >
                            Request a demo
                        </a>
                    </div>
                </div>
            </div>

            {/* Bottom gradient transition */}
            <div className="gradient-overlay-bottom"></div>
        </section>
    );
};

export default HeroSection;
