import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { ChevronDownIcon, Bars3Icon, XMarkIcon } from '@heroicons/react/24/outline';

const Navbar = () => {
    const [isScrolled, setIsScrolled] = useState(false);
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            setIsScrolled(window.scrollY > 10);
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    // Fonction pour remonter en haut de la page
    const scrollToTop = () => {
        window.scrollTo({ top: 0, behavior: 'smooth' });
        setIsMobileMenuOpen(false);
    };

    const platformItems = [
        { name: 'Plateforme Overview', href: '/platform', description: 'Vue d\'ensemble de notre plateforme d\'analytics IA' },
        { name: 'EleyaTwin', href: '/eleyatwin', description: 'Notre plateforme de jumeaux numériques' },
        { name: 'FranceGPT', href: '/francegpt', description: 'IA conversationnelle française' }
    ];

    const solutions = {
        'By Team': [
            { name: 'For Brands', href: '/solutions/brands', description: 'Power niche go-to-market strategies at scale' },
            { name: 'For Research Providers', href: '/solutions/research-providers', description: 'Deliver more with science-backed augmented insights' }
        ],
        'By Use Case': [
            { name: 'Segmentation and U&A', href: '/solutions/segmentation-ua', description: 'Understand hard-to-reach audiences better' },
            { name: 'Claim Testing', href: '/solutions/claim-testing', description: 'Validate claims with comprehensive insights' },
            { name: 'Opinion', href: '/solutions/opinion', description: 'Reliably analyze underrepresented groups' },
            { name: 'Brand Perception and Satisfaction', href: '/solutions/brand-perception', description: 'Unlock granular insights even with fast studies' }
        ]
    };

    const otherPages = [
        { name: 'Blog', href: '/blog', description: 'Insights et actualités' },
        { name: 'Contact', href: '/contact', description: 'Nous contacter' }
    ];

    return (
        <nav className={`fixed top-0 left-0 w-full z-50 transition-all duration-300 ${isScrolled
            ? 'bg-white/95 backdrop-blur-md shadow-sm border-b border-gray-100'
            : 'bg-transparent'
            }`}>
            <div className="w-full max-w-none px-4 sm:px-6 lg:px-8">
                <div className="max-w-7xl mx-auto">
                    <div className="flex items-center justify-between h-16">
                        {/* Logo */}
                        <div className="flex items-center">
                            <Link to="/" className="flex items-center" onClick={scrollToTop}>
                                <div className="w-8 h-8 bg-orange-600 rounded-lg flex items-center justify-center mr-3">
                                    <span className="text-white font-display font-semibold text-lg">E</span>
                                </div>
                                <span className="text-xl font-display font-semibold text-gray-800">ELEYA</span>
                            </Link>
                        </div>

                        {/* Desktop Navigation */}
                        <div className="hidden md:flex items-center space-x-8">
                            {/* Plateforme Dropdown */}
                            <div className="relative group">
                                <button className="flex items-center text-gray-600 hover:text-orange-600 font-sans font-medium transition-colors duration-200">
                                    Plateforme
                                    <ChevronDownIcon className={`ml-1 h-4 w-4 transition-transform duration-200 group-hover:rotate-180`} />
                                </button>

                                <div className="absolute top-full left-0 pt-2 w-80 opacity-0 invisible group-hover:opacity-100 group-hover:visible transition-all duration-200">
                                    <div className="bg-white rounded-xl shadow-lg border border-orange-100 overflow-hidden">
                                        <div className="p-6">
                                            <div className="space-y-3">
                                                {platformItems.map((item) => (
                                                    <Link
                                                        key={item.name}
                                                        to={item.href}
                                                        onClick={scrollToTop}
                                                        className="block p-3 rounded-lg hover:bg-orange-25 transition-colors duration-200 group/item"
                                                    >
                                                        <div className="font-display font-medium text-gray-800 group-hover/item:text-orange-600 transition-colors duration-200">
                                                            {item.name}
                                                        </div>
                                                        <div className="text-sm text-gray-500 mt-1 font-sans">
                                                            {item.description}
                                                        </div>
                                                    </Link>
                                                ))}
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>

                            {/* Solutions Dropdown */}
                            <div className="relative group">
                                <button className="flex items-center text-gray-600 hover:text-orange-600 font-sans font-medium transition-colors duration-200">
                                    Solutions
                                    <ChevronDownIcon className={`ml-1 h-4 w-4 transition-transform duration-200 group-hover:rotate-180`} />
                                </button>

                                <div className="absolute top-full left-0 pt-2 w-96 opacity-0 invisible group-hover:opacity-100 group-hover:visible transition-all duration-200">
                                    <div className="bg-white rounded-xl shadow-lg border border-orange-100 overflow-hidden">
                                        <div className="p-6">
                                            {Object.entries(solutions).map(([category, items]) => (
                                                <div key={category} className="mb-6 last:mb-0">
                                                    <h3 className="text-sm font-display font-semibold text-gray-800 mb-3 uppercase tracking-wide">
                                                        {category}
                                                    </h3>
                                                    <div className="space-y-3">
                                                        {items.map((item) => (
                                                            <Link
                                                                key={item.name}
                                                                to={item.href}
                                                                onClick={scrollToTop}
                                                                className="block p-3 rounded-lg hover:bg-orange-25 transition-colors duration-200 group/item"
                                                            >
                                                                <div className="font-display font-medium text-gray-800 group-hover/item:text-orange-600 transition-colors duration-200">
                                                                    {item.name}
                                                                </div>
                                                                <div className="text-sm text-gray-500 mt-1 font-sans">
                                                                    {item.description}
                                                                </div>
                                                            </Link>
                                                        ))}
                                                    </div>
                                                </div>
                                            ))}
                                        </div>
                                    </div>
                                </div>
                            </div>

                            {/* Other Pages */}
                            {otherPages.map((page) => (
                                <Link
                                    key={page.name}
                                    to={page.href}
                                    onClick={scrollToTop}
                                    className="text-gray-600 hover:text-orange-600 font-sans font-medium transition-colors duration-200"
                                >
                                    {page.name}
                                </Link>
                            ))}
                        </div>

                        {/* CTA Buttons */}
                        <div className="hidden md:flex items-center space-x-4">
                            <Link
                                to="/demo"
                                onClick={scrollToTop}
                                className="bg-orange-600 hover:bg-orange-700 text-white px-6 py-2 rounded-lg font-display font-medium transition-all duration-200 shadow-sm hover:shadow-md"
                            >
                                Request a Demo
                            </Link>
                        </div>

                        {/* Mobile menu button */}
                        <div className="md:hidden">
                            <button
                                onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                                className="text-gray-600 hover:text-orange-600 transition-colors duration-200"
                            >
                                {isMobileMenuOpen ? (
                                    <XMarkIcon className="h-6 w-6" />
                                ) : (
                                    <Bars3Icon className="h-6 w-6" />
                                )}
                            </button>
                        </div>
                    </div>

                    {/* Mobile Navigation */}
                    {isMobileMenuOpen && (
                        <div className="md:hidden bg-white border-t border-gray-100 shadow-lg">
                            <div className="px-4 py-6 space-y-4">
                                {/* Platform mobile */}
                                <div className="border-b border-gray-100 pb-4">
                                    <h3 className="text-sm font-display font-semibold text-gray-800 mb-3">Plateforme</h3>
                                    {platformItems.map((item) => (
                                        <Link key={item.name} to={item.href} className="block text-gray-600 hover:text-orange-600 font-sans py-2 pl-4" onClick={scrollToTop}>
                                            {item.name}
                                        </Link>
                                    ))}
                                </div>

                                {/* Solutions mobile */}
                                <div className="border-b border-gray-100 pb-4">
                                    <h3 className="text-sm font-display font-semibold text-gray-800 mb-3">Solutions - By Team</h3>
                                    {solutions['By Team'].map((item) => (
                                        <Link key={item.name} to={item.href} className="block text-gray-600 hover:text-orange-600 font-sans py-2 pl-4" onClick={scrollToTop}>
                                            {item.name}
                                        </Link>
                                    ))}
                                </div>

                                <div className="border-b border-gray-100 pb-4">
                                    <h3 className="text-sm font-display font-semibold text-gray-800 mb-3">Solutions - By Use Case</h3>
                                    {solutions['By Use Case'].map((item) => (
                                        <Link key={item.name} to={item.href} className="block text-gray-600 hover:text-orange-600 font-sans py-2 pl-4" onClick={scrollToTop}>
                                            {item.name}
                                        </Link>
                                    ))}
                                </div>

                                {/* Other Pages mobile */}
                                <div className="border-b border-gray-100 pb-4">
                                    <h3 className="text-sm font-display font-semibold text-gray-800 mb-3">Autres Pages</h3>
                                    {otherPages.map((page) => (
                                        <Link key={page.name} to={page.href} className="block text-gray-600 hover:text-orange-600 font-sans py-2 pl-4" onClick={scrollToTop}>
                                            {page.name}
                                        </Link>
                                    ))}
                                </div>

                                <div className="pt-4">
                                    <Link to="/demo" className="block bg-orange-600 hover:bg-orange-700 text-white px-6 py-3 rounded-lg font-display font-medium text-center transition-all duration-200" onClick={scrollToTop}>
                                        Request a Demo
                                    </Link>
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
