import React, { useEffect, useRef, useState } from 'react';

const CounterCard = ({ number, suffix, title, description, delay = 0, noAnimation = false }) => {
    const [isVisible, setIsVisible] = useState(false);
    const [count, setCount] = useState(noAnimation ? number : 0);
    const cardRef = useRef(null);

    useEffect(() => {
        const observer = new IntersectionObserver(
            ([entry]) => {
                if (entry.isIntersecting) {
                    setTimeout(() => {
                        setIsVisible(true);
                        // Start counting animation only if noAnimation is false
                        if (!noAnimation) {
                            const duration = 2000; // 2 seconds
                            const steps = 60;
                            const increment = number / steps;
                            let current = 0;

                            const timer = setInterval(() => {
                                current += increment;
                                if (current >= number) {
                                    setCount(number);
                                    clearInterval(timer);
                                } else {
                                    setCount(Math.floor(current));
                                }
                            }, duration / steps);
                        }
                    }, delay);
                }
            },
            { threshold: 0.1 }
        );

        if (cardRef.current) {
            observer.observe(cardRef.current);
        }

        return () => observer.disconnect();
    }, [number, delay, noAnimation]);

    return (
        <div
            ref={cardRef}
            className={`text-center transition-all duration-1000 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'
                }`}
        >
            <div className="text-4xl sm:text-5xl lg:text-6xl font-display font-semibold text-[#194471] mb-2">
                {count.toLocaleString()}{suffix}
            </div>
            <h3 className="text-xl font-display font-semibold text-gray-800 mb-2">
                {title}
            </h3>
            <p className="text-gray-600 font-sans">
                {description}
            </p>
        </div>
    );
};

const FiguresSection = () => {
    const [titleVisible, setTitleVisible] = useState(false);
    const titleRef = useRef(null);

    useEffect(() => {
        const observer = new IntersectionObserver(
            ([entry]) => {
                if (entry.isIntersecting) {
                    setTitleVisible(true);
                }
            },
            { threshold: 0.1 }
        );

        if (titleRef.current) {
            observer.observe(titleRef.current);
        }

        return () => observer.disconnect();
    }, []);

    const figures = [
        {
            number: 80,
            suffix: '%',
            title: 'Cost reduction',
            description: 'Access niche audiences at a fraction of the cost'
        },
        {
            number: 1,
            suffix: 'h',
            title: 'Results in real-time',
            description: 'Get results in real-time'
        },
        {
            number: 150000,
            suffix: '',
            title: 'Digital twins simulated',
            description: 'More than 150,000 digital twins already simulated'
        }
    ];

    return (
        <section className="relative py-20" style={{
            backgroundColor: 'transparent'
        }}>
            {/* Dégradé de transition du haut */}
            <div className="absolute top-0 left-0 right-0 h-20 bg-gradient-to-b from-white/40 via-white/60 to-white/75 z-10"></div>

            {/* Contenu principal avec fond semi-transparent */}
            <div className="relative z-20" style={{
                backgroundColor: 'rgba(255, 255, 255, 0.75)',
                backgroundImage: 'radial-gradient(rgba(254, 195, 147, 0.08) 1px, transparent 1px)',
                backgroundSize: '20px 20px',
                margin: '20px 0'
            }}>
                <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-20">
                    {/* Header */}
                    <div
                        ref={titleRef}
                        className={`text-center mb-16 transition-all duration-1000 ${titleVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'
                            }`}
                    >
                        <p className="text-[#194471] text-sm font-medium uppercase tracking-wide mb-4 font-sans">
                            Market Research
                        </p>
                        <h2 className="text-3xl sm:text-4xl lg:text-5xl font-display font-semibold text-gray-800 mb-6">
                            Proven results that drive growth
                        </h2>
                        <p className="text-lg text-gray-600 max-w-3xl mx-auto font-sans">
                            Experience the power of AI-driven surveys that deliver unprecedented quality and speed for your research needs.
                        </p>
                    </div>

                    {/* Figures Grid */}
                    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8 lg:gap-12">
                        {figures.map((figure, index) => (
                            <CounterCard
                                key={index}
                                number={figure.number}
                                suffix={figure.suffix}
                                title={figure.title}
                                description={figure.description}
                                delay={index * 200}
                                noAnimation={figure.title === 'Results in real-time'}
                            />
                        ))}
                    </div>
                </div>
            </div>

            {/* Dégradé de transition du bas */}
            <div className="absolute bottom-0 left-0 right-0 h-20 bg-gradient-to-t from-white/40 via-white/60 to-white/75 z-10"></div>
        </section>
    );
};

export default FiguresSection;
